mapper.select:

WHERE 1=1
AND
(

    a.state = #{state}

)

  AND  (t1.id = #{typeId} OR  t2.id = #{typeId})


  AND (a.title LIKE '%${search}%' OR  a.label LIKE '%$(search)%')


  AND a.type = #{shareType}


  AND platform.id = #{patformId}


  AND a.user_id = #{userId}


    ORDER BY ${order}

LIMIT #{offset},#{rows}
